function showProblem(n, thenDo) {
  // this.submorphs.pluck("name")
  // this.fetchProblem(118, show.curry("%s %o"))
  // this.showProblem(20, show.curry("%s %o"))
  // this.showProblem(3, show.curry("%s %o"))

  var self = this;

  lively.lang.fun.composeAsync(
    self.fetchProblem.curry(n),
    show
  )(thenDo);

  function show(data, thenDo) {
    self.no = Number(n);
    var descr = '<div class="descr">' + data.description
      + (data.restricted && data.restricted.length ?
        "\n\n<p>Note: You are not allowed to use the functions <code>"
          + data.restricted.join("</code>, <code>") + "</code>!</p>" : "") + '</div>';
    self.get("title").textString = data.title;
    self.get("notes").textString = "#" + n + " | author: " + data.user + " | difficulty: " + data.difficulty + " | solved: " + data["times-solved"] + "x";

    self.get("testCode").textString = Array.isArray(data.tests) ? data.tests.join("\n") : data.tests;
    self.get("solutionCode").textString = "; What code needs to substitute __ in the tests above?\n"
    self.get("solutionCode").focus();
    self.get("description").setHTML(descr);

    // self.get("code").textString = Array.isArray(data.tests) ? data.tests.join("\n") : data.tests;
    thenDo(null, data);
  }
}

