function runTestsLocally() {
  // this.get("testCode").setInputAllowed(false);
  var solution = this.get("solutionCode");
  var test = this.get("testCode");
  this.get("solutionCode").hideStatusMessage()
  var ns = Global.clojure.Runtime.detectNs(test);
  var env = Global.clojure.Runtime.currentEnv(test);

  var solutionCode = this.getSolutionCode();  
  if (!solutionCode) {
    test.removeTextOverlay();
    solution.hideStatusMessage();
    return;
  }

  lively.lang.fun.composeAsync(
    defineNs,
    defineSubs,
    runTests
  )(function(err, result) {
    if (err) {
      var errString = String(err).split("\n").uniq().join("\n");
      var msg = [
        ["open full stack trace\n", {doit: {context: {env: env, ns: ns, err: err}, code: errorRetrieval}}],
        [errString.truncate(300)]];
      solution.setStatusMessage(msg, Global.Color.red);
    }
    // show(err ? String(err) : result)
  });

  function runTests(_, thenDo) {
    var code = test.textString;
    test.setInputAllowed(true)
    test.removeTextOverlay()
    test.aceEditor.execCommand("clojureDoLiveEval");
    test.setInputAllowed(true)
    thenDo(null);
  }

  function defineNs(thenDo) {
    var code = lively.lang.string.format("(ns %s)",ns);
    Global.clojure.Runtime.doEval(code, {env: env, ns: null,passError: true}, thenDo);
  }

  function defineSubs(_, thenDo) {
    var code = lively.lang.string.format("(def __ \n (eval (read-string \"%s\")))",
      solutionCode
              .replace(/\\([^"])/g, '\\\\$1')
              .replace(/\\"/g, '\\\\\\"')
              .replace(/""/g, '\\"\\"')
              .replace(/([^\\])"/g, '$1\\"'));
    Global.clojure.Runtime.doEval(code, {
      env: env, ns: ns, passError: true
    }, thenDo);
  }


  var errorRetrieval = lively.lang.fun.extractBody(function() {
    // simply printing what we have
    // lively.ide.codeeditor.modes.Clojure.update()
    var ed = $world.addCodeEditor({
      extent: pt(700, 500),
      title: "clojure stack trace",
      textMode: "text",
      content: String(this.err)
    }).getWindow().comeForward();
  })
}

