function getSolutionCode() {
  var solution = this.get("solutionCode");
  var nodes = solution.aceEditor.session.$ast.children;
  return nodes.reduce(function(code, n) {
    return n.type = "comment" ?
      code.slice(0,n.start) + code.slice(n.end) :
      code;
  }, solution.textString).trim();
}

