function showResult(result, thenDo) {
  var resultScreen = this.getMorphNamed("resultScreen");
  if (resultScreen) resultScreen.remove();

  var self = this;
  resultScreen = lively.morphic.Text.makeLabel("", rect(0,0, 600, 300));
  resultScreen.name = "resultScreen";
  resultScreen.applyStyle({
    align: "center", fill: Global.Color.white,
    fontFamily: "geneva,sans-serif",
    fontSize: 16, inputAllowed: false, selectable: false
  });


  // result = {error: " fooo"}
  // result = {error: ""}
  if (result.error) {
    resultScreen.setRichTextMarkup([
      ["Not a valid solution!\n" + result.error, { color: Global.Color.red }],
      ["\n\n"],
      ["Try again", {doit: {context: {message: resultScreen}, code: "this.message.remove();"}}],
      ["\nor\n"],
      ["Try another problem", {doit: {context: {message: resultScreen}, code: "this.message.remove();"}}]
    ]);
  } else {
    resultScreen.setRichTextMarkup([
      ["Success!!!\nYou did it!", { color: Global.Color.green }],
      ["\n\n"],
      ["Back to solution", {doit: {context: {message: resultScreen}, code: "this.message.remove();"}}],
      ["\nor\n"],
      ["Solve another problem", {doit: {context: {message: resultScreen}, code: "this.message.remove();"}}]
    ]);
  }

  resultScreen.openInWorld();
    resultScreen.fitThenDo(function() {
      var m = $world.addModal(resultScreen, self.getWindow());
      m.align(m.bounds().center(), m.owner.innerBounds().center());
      // (function() { m.remove(); }).delay(6);
    })
}
