function project_doInstallOrBuild(project, action, thenDo) {
  var self = this;
  // var installOrDeploy = "install" || "deploy";
  action = action || "install";
  if (action === "deploy") action = "deploy clojars";

  lively.lang.fun.composeAsync(
    function(n) {
      lively.require('lively.ide.tools.ShellCommandRunner')
        .toRun(function() { n(); });
    },
    function fetchConfig(n) {
      var code = lively.lang.string.format(
        '(rksm.cloxp-projects.core/find-project-configuration-file "%s")',
        project.dir);
      Global.clojure.Runtime.doEval(code, {requiredNamespaces: ["rksm.cloxp-projects.core"], resultIsJSON: false, passError: true}, function(err, config) {
        try { config = eval(config); } catch (e) {}
        config = config && config.trim() !== "nil" ? config.trim() : null;
        n(config ? null : new Error("Dont know how"), config); });
    },

    function doInstall(config, n) {
      if (!config) return n(new Error("no config found!"));
      var cmdString = null;
      if (config.match(/pom.xml$/))
        cmdString = "cd " + project.dir + "; mvn " + action;
      if (config.match(/project.clj$/))
        cmdString = "cd " + project.dir + "; lein do clean, test, " + action;
      if (!cmdString)
        return n(new Error("Cannot install from config " + config));
      lively.ide.tools.ShellCommandRunner.run(cmdString, {cwd: project.dir}, n)
    }

  )(function(err, cmd) {
    err = err || (cmd.getCode() && cmd.resultString(true));
    err && self.setStatusMessage("Error trying to install " + project.dir + ":\n" +
      String(err).truncate(300));
    thenDo && thenDo(err, cmd);
  })
}

