function projects_build(projects, action) {
  // this.buildMany();
  // var selections = this.selectedProjects();

  var self = this;

  lively.lang.fun.composeAsync(
    function(n) { n(null, projects); },
    function getDeps(selections, next) {
      var fetchProjects = !selections.every(function(ea) { return ea.hasOwnProperty("dependencies"); });
      self.projects_topoSortByDeps(selections, true/*onlyLocal*/, fetchProjects, next);
    },
    function build(projects, n) {
        var pBar = $world.addStatusProgress("building " + projects.length + " projects...");
        projects.mapAsyncSeries(function(proj, i, n) {
