function fetchProjectInfos(projects, thenDo) {

  lively.lang.arr.mapAsyncSeries(projects, function(p, _, next) {
    lively.lang.fun.composeAsync(
      manualDeps,
      getPackageJSON, packageJSONToProjectInfo,
      cljProjectInfo
    )(p, next)
  }, function(err, projects) {
    if (err) show("fetchProjectInfos error: %s", err);
    thenDo && thenDo(err, projects);
  });

  function getPackageJSON(p, thenDo) {
    lively.shell.cat(p.dir + "/package.json", {}, function(err, source) {
      if (!err) try { var json = JSON.parse(source); } catch (e) {}
      thenDo(null, p, json || {});
    });
  }

  function packageJSONToProjectInfo(p, packageJson, thenDo) {
    if (!packageJson) return thenDo(null, p);
    if (!p.name) p.name = packageJson.name;
    var deps = uniqDeps((p.dependencies || [])
      .concat(packageJson.dependencies || [])
      .concat(packageJson.devDependencies || [])
      .concat(packageJson.optionalDependencies || []));
    lively.lang.obj.extend(p,
      {version: packageJson.version, dependencies: deps});
    thenDo(null, p);
  }

  function manualDeps(p, thenDo) {
    p.dependencies = (p.dependencies || []).concat(p.deps || []);
    thenDo(null, p);
  }

  function cljProjectInfo(p, thenDo) {
    var code = "(rksm.cloxp-projects.lein/project-info->json \"" + p.dir + "\" {:include-plugins? true, :clean? true, :include-dev? true})"
    Global.clojure.Runtime.doEval(
      code,
      {requiredNamespaces: ["rksm.cloxp-projects.lein"], resultIsJSON: true, passError: true},
      function(err, result) {
        if (err || !result) return thenDo(err, p);
        var deps = (result.dependencies || []).map(function(ea) {
          var pair = ea[0].split("/");
          return {name: pair.last(), group: pair.first(), version: ea[1]};
        });

        if (!p.name) p.name = result.name;
        lively.lang.obj.extend(p, {
          group: result.group,
          description: result.description,
          version: result.version,
          cljName: result.name,
          dependencies: uniqDeps((p.dependencies || []).concat(deps))
        });

        thenDo(null, p);
      });
  }

  function uniqDeps(deps) {
    return deps.uniqBy(function(a,b) {
      return (a.name || a) === (b.name || b); });
  }
}

