function startReading() {
  var self = this;
  var s = lively.net.SessionTracker.getSession();
  s.send(
    "nreplLogStartReading",
    {nreplOptions: Global.clojure.Runtime.currentEnv()},
    function(answer) {
      if (answer.expectMoreResponses && !self.world()) self.stopReading()
      else self.onLogMessage(answer.data);
  });
}

