function onLogMessage(logMessages) {
  var log = this.get("logText");
  var printed = logMessages.map(function(l) {
    var s;
    try {
      var json = typeof l.message === 'object' ? l.message : JSON.parse(l.message);
      s = JSON.stringify(json, null, 2);
    } catch (e) { s = l.message; }
    s = s.replace(/^\{\n|\n\}$/mg, "").replace(/^\s+/mg, "");
    return lively.lang.string.format("[%s%s]\n%s\n",
      lively.lang.date.format(new Date(l.time), "HH:MM:ss"),
      l.length ? ", " + l.length + " bytes" : "", s);
  }).join("");


  var s = log.textString + printed;

  if (s.length > this.maxSize) {
    s = s.slice(-this.maxSize);
    log.withAceDo(function(ed) {
      ed.session.getUndoManager().reset();
    });
  }

  log.saveExcursion(function(reset) {
    log.textString = s;
    reset();
  });

  (function() {
    if (log.get("FollowLogCheckBox").isChecked()) {
      log.withAceDo(function(ed) {
        ed.gotoLine(ed.session.getLength(), 0, true);
      });
    }
  }).delay(0);
}

