    var keys         = evt.getKeyString(),
        handled      = false;

    switch (keys) {
      case "Alt-L": 
        this.get("FollowLogCheckBox").setChecked(
          !this.get("FollowLogCheckBox").isChecked());
        handled = true;
        break;
    }

    if (!handled) return $super(evt);
    evt.stop(); return true;
}

