function selectEntry(entry) {
    if (!entry) return;
    var self = this;
    self.get("CurrentEntry").textString = entry.fullPath;
    if (entry.isDirectory) {
        var dirReader = entry.createReader();
        self.get("Entries").setList([]);
        // Call reader.readEntries() until no more results are returned.
        var readEntries = function() {
            dirReader.readEntries (function(results) {
                if (results.length) {
                    results.forEach(function(dirEntry){
                        dirEntry.getMetadata(function(metadata){
                            self.get("Entries").addItem({
                                string: dirEntry.name + ' (' + metadata.size + ' Bytes)', 
                                value: dirEntry});
                        });
                    });
                    readEntries(); // read more
                }
            }, function(err){self.get("CurrentEntry").textString = err.message});
        };
        readEntries(); // Start reading
    }
}

