function removeEntry() {
    var self = this,
        entry = self.get("Entries").selection;
    if (!entry) return;
    $world.confirm("Delete " + entry.name + "?", function(yes){
        if (!yes) return;
        entry.getParent(function(parent){
            entry.remove(function(){
                self.selectEntry(parent)
            }, function(e){alert("remove(): " + e.message)});
        }, function(e){alert("getParent(): " + e.message)});
    });
}

