function refresh() {
    var self = this;
    self.reset();
    self.get("FS").textString = "looking for persistent storage";
    window.webkitRequestFileSystem(PERSISTENT, 5*1024*1024, function(fs) {
        self.get("FS").textString = fs.root.toURL();
        self.selectEntry(fs.root);
    }, function(err){
        self.get("FS").textString = err;
    });
}
