140.source:
function checkLibrary(cb) {
    if (Global.Benchmark) {
        return setTimeout(cb, 100);
    }
    var head = document.getElementsByTagName('head')[0];
    var script = document.createElement('script');
    script.type = 'text/javascript';
    script.src = "http://lively-kernel.org/repository/webwerkstatt/users/cschuster/benchmark.js";
    head.appendChild(script);
    setTimeout(cb, 3000);
}

143.source:
function start() {
    var status = this.get("Results");
    var setup = this.get("SetupCode").getTextString();
    var test = this.get("TestCode").getTextString();
    var bench = new Benchmark({
        'setup': setup, 
        'fn': test
    });
    bench.on('cycle', function() {
        if (bench.running) {
            status.appendTextString(".");
        }
    });
    bench.on('complete', function() {
        if (bench.error) {
            status.setTextString("Error: " + bench.error);
            return;
        }
        var result = 'Ran ' + Benchmark.formatNumber(bench.count) + ' samples in ';
        result += Benchmark.formatNumber(bench.times.elapsed.toFixed(1)) + ' s\n';
        var unit = "s";
        var mean = bench.stats.mean;
        if (mean < 1) { mean *= 1000; unit = "ms"; }
        if (mean < 1) { mean *= 1000; unit = "μs"; }
        if (mean < 1) { mean *= 1000; unit = "ns"; }
        if (mean < 10) {
            mean = mean.toFixed(2);
        } else if (mean < 100) {
            mean = mean.toFixed(1);
        } else {
            mean = mean.toFixed(0);
        }
        result += "Average test time: " + mean + " " + unit + '\n';
        result += 'Relative margin of error: ± ' + bench.stats.rme.toFixed(2) + ' %';
        status.setTextString(result);
    });
    bench.run({async: true, queued: true, maxTime:3});
}
