function setModuleName(name) {
    var metaInfo = this.getPartsBinMetaInfo();
    metaInfo.requiredModules = [];
    if (name) {
        this.getPartsBinMetaInfo().addRequiredModule(name)
        this.moduleName = name;
    }
}

