function moduleNameWasChanged(newName) {
    this.updateStatus(newName);
    var valid = this.isValidModuleName(newName);
    this.setModuleName(valid ? newName : null);
}

106.source:
function loadModule() {
    var name = this.moduleName,
        m = module(name),
        self = this;
    if (m.isLivelyModule && m.isLoaded()) {
        alertOK(name + ' already loaded');
        return;
    }

    var success = false, waitSec = 3;
    (function checkLoad() {
        if (success) return;
        alert(name + ' not loaded after ' + waitSec
            + ' seconds! There is probably a problem.');
    }).delay(waitSec);

    require(name).toRun(function() {
        // FIXME callback gets also triggered when after trying
        // to load a non-existing module more than once!!!
        self.updateStatus(name);
        if (!m.isLivelyModule || !m.isLoaded()) return;
        success = true;
        alertOK(name + ' is now loaded');
    });

}

110.source:
function updateStatus(moduleName) {
    var valid = this.isValidModuleName(moduleName),
        status = this.get('statusText');
    if (!valid) {
        status.setTextString('not a valid module name');
        status.setTextColor(Color.red);
        return;
    }
    var m  = module(moduleName);
    if (m.isLivelyModule && m.isLoaded()) {
        status.setTextString('is loaded');
        status.setTextColor(Color.green);
    } else {
        status.setTextString('is not loaded');
        status.setTextColor(Color.black);
    }
}

