function describe(obj) {
    var str;
    if (obj && obj.name) {
        str = Object.isFunction(obj.name) ? obj.name() : obj.name;
    }
    if (!str) str = Objects.shortPrintStringOf(obj);
    if (str.length > 32) str = str.substring(0, 36) + '...';
    return str;
}

