function reset() {
    this.get('MethodSource').textString = '';
    this.get('MethodsList').setList([]);
    this.setPartUnderTest(null);
    this.doNotSerialize = ['currentTestCase']
    // this.owner.minExtent = this.owner.getExtent();
    this.displayResult('', null);
}

