function setPartUnderTest(aPart) {
    this.partUnderTest = aPart;
    this.updateMethodsList();
    if (this.owner && this.owner.isWindow ) {
        if (aPart && aPart.name) {
            this.owner.setTitle('PartTestRunner: ' + aPart.name);
        } else {
            this.owner.setTitle('PartTestRunner');
        }
    }
}

