function runTestsAndReport() {
    var part = this.getPartUnderTest();
    if (!part) {
        return;
    }
    var startTime = new Date();
    var result = part.runPartTests();
    var time = new Date() - startTime;
    var infoString = time.toString() + 'ms for ' 
    infoString += (result.failed.length + result.succeeded.length) + ' tests';

    if (result.failed.length == 0) {
        this.displayResult(infoString, Color.green);
    } else {
        var failString = infoString + '\n\n';
        failString += result.failed.length + ' failed tests:\n\n';
        failString += result.failed.collect(function(failed) {
            var msg = failed.classname + '>>' + failed.selector;
            msg += '\n' + failed.err.message;
            msg += '\n' + failed.err.stack;
            return msg;
        }).join('\n---------\n');
        this.displayResult(failString, Color.red);
    }
}

