function reset() {
    this.get('MethodSource').textString = '';
    this.get('MethodsList').setList([]);
    this.setPartUnderTest(null);
    this.doNotSerialize = ['currentTestCase']
    // this.owner.minExtent = this.owner.getExtent();
    this.displayResult('', null);
}

172.source:
function setPartUnderTest(aPart) {
    this.partUnderTest = aPart;
    this.updateMethodsList();
    if (this.owner && this.owner.isWindow ) {
        if (aPart && aPart.name) {
            this.owner.setTitle('PartTestRunner: ' + aPart.name);
        } else {
            this.owner.setTitle('PartTestRunner');
        }
    }
}

178.source:
function addTestMethod() {
    var part = this.getPartUnderTest(),
        func = this.getCurrentTestMethod();
    if (!part || !func) {
        return;
    }
    part.addPartTest(func);
    this.updateMethodsList();
}

181.source:
function removeTestMethod() {
    var part = this.getPartUnderTest();
    part.removePartTest(this.get('MethodsList').getSelection());
    this.updateMethodsList();
}

184.source:
function getCurrentTestMethod() {
    var code = '(' + this.get('MethodSource').textString + ')',
        func;
    try {
        func = eval(code);
    } catch (e) {
        alert('error evaluating test method');
        return null;
    }
    if (!func || !func.constructor || !(func.constructor.name === 'Function')) {
        alert('error evaluating test method');
        return null;
    }
    return func;
}

