function openTestMethod(aSelector) {
    var part = this.getPartUnderTest(),
        testsObject;
    if (!part) {
        return;
    }
    testsObject = part.ensurePartTestsObject();
    if (testsObject[aSelector]) {
        this.get('MethodSource').setTextString(testsObject[aSelector].toString());
    }
}

