function printClass(c) {
    if (!c) return;
    return "### " + (c.displayName || c.name) + " ###\n"+ 
        Properties.own(c.categories).sort().collect(function(name) {
            var methods = c.categories[name];
            return "" + name + ":\n" + methods.sort().join(',  ')
        }).join("\n")
}

