function setTarget(object) {
    this.targetObject = object
    var text = this.get("TextAPIText")
    text.textString = this.printProtocoll(this.targetObject.constructor)
    text.emphasizeAll({color: Color.darkGray})
    text.emphasizeRegex(/#.*#/g, {color: Color.blue, fontSize: 16})
    text.emphasizeRegex(/.*\:/g, {color: Color.green})
}

