function displayOverview(target) {
    // this.displayOverview($world)
    alertOK("display " + target)
    var code =''
    if (target) {
        target.withAllSubmorphsDo(function(ea) {
            if (this.partsBinMetaInfo && this.partsBinMetaInfo.requiredModules.length > 0) {
                code += '// ' + ea.name + 'require ' +  this.partsBinMetaInfo.requiredModules   
            }

            Functions.own(ea).each(function(scriptName) {
                var script = ea[scriptName]
                if (script.getOriginal) {
                    code +=  "$morph('" + ea.name +"')" +
                        Strings.format('.addScript(%s)', script.getOriginal()) + '\n\n'
                }
            })
        })
    }
    this.get('AllScriptsText').textString = code
}

