function update() {
    var space = this.getExtent().addXY(0,1);
    var run = this.succeeded + this.failed;
    var ratio = this.total === 0 ? 0 : run / this.total;
    this.get("ProgressBar").setExtent(space.scaleByPt(lively.pt(ratio, 1)));
    this.get("ProgressBar").setFill(this.failed > 0 ? Color.rgb(128, 0, 0) : Color.rgb(0, 128, 0));
    this.get("ProgressText").setExtent(space);
    if (this.total === 0) {
        this.get("ProgressText").textString = '';
    } else {
        this.get("ProgressText").textString = run + ' run, ' + this.failed + ' failed';
    }
}

