function runBenchmarks(iterations) {
    var self = this,
        testList = self.get("TestList"),
        progress = this.get("Progress"),
        testSuite = [];
    if (testList.isRunning) {
        testList.stopTests();
    }
    testList.isRunning = true;
    var root = testList.selectedNode || testList;
    root.withAllItemsDo(function(item) {
        if (item.test) {
            item.message = "";
            testSuite.push(item);
        }
    }, testList);
    this.get("Progress").setTotal(testSuite.length * 10);
    var runners = [];
    var idx = 0;
    testSuite.each(function (ea) {
        var times = [];
        for (var i = 0; i < iterations; i++) {
            runners.push(function() {
                if (!testList.isRunning) return;
                ea.test.setUp();
                var time = performance.now()
                try {
                    ea.test[ea.test.currentSelector]();
                    progress.success();
                } catch(e) {
                    ea.message = "Error " + e;
                    progress.failure();
                }
                times.push(performance.now() - time);

                idx += 1;
                setTimeout(runners[idx], 10);
            });
        }
        runners.push(function () {
            ea.message = "Avg: " + (times.sum() / times.length) + "\n" +
                        "Std Deviation: " + self.stdDeviation(times) + "\n" +
                        ea.message;
            idx += 1;
            setTimeout(runners[idx], 10);
        });
    });
    runners.push(function () {
        testList.isRunning = false;
    });
    runners[idx]();
}

