function addTest(testCase) {
    var path = testCase.constructor.type.split('.');
    var parentItem = this.lookupPath(this.item, path);
    var item = parentItem.children.find(function(i) {
        return i.name === testCase.currentSelector;
    });
    if (!item) {
        item = this.createTestItem(parentItem, testCase);
    } else {
        item.test = testCase;
        item.changed();
    }
    testCase.statusUpdateFunc = this.testStatus.bind(this, item);
}

