function removeModule(name) {
    var toRemove = [];
    this.withAllItemsDo(function(item) {
        if (item.test) {
            var mod = item.test.constructor.sourceModule;
            if (mod.name() === name) toRemove.push(item);
        }
    });
    toRemove.each(function(i) {
        this.dec('total', i);
    }, this);
}

