function lookupPath(parent, path) {
    if (path.length === 0) return parent;
    var current = path.shift();
    var child = parent.children.find(function(c) {
        return c.name === current;
    });
    if (!child) {
        child = this.createPathItem(parent, current, path.length > 0);
    }
    return this.lookupPath(child, path);
}

