function createItem(parent, spec) {
    var item = Object.clone(spec);
    item.total = 0;
    item.succeeded = 0;
    item.failed = 0;
    item.pending = 0;
    item.running = 0;
    item.time = 0;
    item.parent = parent;
    Object.addScript(item, function onSelect() {});
    Object.addScript(item, function changed() {});
    parent.children.push(item);
    parent.children = parent.children.sortBy(function(i) { return i.name; });
    parent.changed();
    return item;
}

