function runSelectedTests() {
    if (this.isRunning) return;
    this.isRunning = true;
    var root = this.selectedNode || this;
    var testSuite = [];
    root.withAllItemsDo(function(item) {
        if (item.test) {
            if (item.succeeded) {
                this.dec('succeeded', item);
                this.dec('time', item, item.time);
            } else if (item.failed) {
                this.dec('failed', item);
            } else if (item.pending) {
                this.dec('pending', item);
            }
            this.inc('pending', item);
            testSuite.push(item.test);
        }
    }, this);
    this.get("Progress").setTotal(testSuite.length);
    testSuite.doAndContinue(this.runNext, this.runFinished.bind(this), this);
}

