function updateTest(item) {
    var style = {};
    if (item.running > 0) {
        style.fontWeight = 'bold';
    } else {
        style.fontWeight = 'normal';
    }
    if (item.pending > 0) {
        style.color = Color.rgb(128, 128, 0);
    } else if (item.failed > 0) {
        style.color = Color.rgb(128, 0, 0);
    } else if (item.succeeded === item.total) {
        style.color = Color.rgb(0, 128, 0);
    } else { // not yet run
        style.color = Color.rgb(0, 0, 0);
    }
    style.hover = {
        inAction: function() {
            tree.showInfoBox(item);
        }.asScript({tree: this, item: item}),
        outAction: function() {
            tree.hideInfoBox();
        }.asScript({tree: this})
    };
    item.style = style;
    item.changed();
}

