function dec(prop, current, amount) {
    var parent = current.parent;
    if (!parent) return;
    current[prop] -= (amount || 1);
    if (current.total === 0) {
        parent.children.remove(current);
        parent.changed();
    } else {
        this.updateTest(current);
    }
    this.dec(prop, parent);
}

291.source:
function inc(prop, current, amount) {
    var parent = current.parent;
    if (!parent) return;
    current[prop] += (amount || 1);
    this.updateTest(current);
    this.inc(prop, parent);
}

