function testStatus(item, test, status, message, error) {
    if (status === "running") {
        this.inc('running', item);
    } else if (status === "success") {
        this.dec('running', item);
        this.dec('pending', item);
        this.inc('succeeded', item);
        this.inc('time', item, test.result.getTimeToRun());
        item.message = '';
        this.get('Progress').success();
    } else if (status === "failure") {
        this.dec('running', item);
        this.dec('pending', item);
        this.inc('failed', item);
        item.message = test.result.failureList().join('');
        this.get('Progress').failure();
    }
}

