function showInfoBox(item) {
    if (!this.infoBox) {
        this.infoBox = new lively.morphic.Text(lively.rect(0,0,300,40));
        this.infoBox.applyStyle({
            fill: Color.rgb(255, 255, 255),
            padding: lively.rect(10, 10, 0, 0),
            borderRadius: 8,
            borderWidth: 1,
            borderColor: Color.rgb(0, 0, 0),
            fixedWidth: false,
            fixedHeight: false,
            wordBreak: 'normal'
        });
        this.infoBox.disableEvents();
    }
    this.infoBox.openInWorld();
    var pos = this.world().firstHand().getPosition();
    this.infoBox.setPosition(pos.addXY(-20, 20));
    this.infoBox.textString = '';
    this.infoBox.appendRichText(item.name + '\n', {fontWeight: 'bold'});
    if (item.time) {
        var style = {color: Color.gray.darker()};
        this.infoBox.appendRichText(item.time + 'ms\n', style);
    }
    if (item.test) {
        if (item.message) {
            var mono = {
                fontFamily: 'Droid Sans mono, DejaVu Sans mono, monospace',
                fontSize: 9
            };
            this.infoBox.appendRichText('\n' + item.message, mono);
        }
    } else {
        var str = item.succeeded + ' / ' + item.total + ' succeeded';
        var color = item.succeeded === item.total ? Color.green : Color.red;
        this.infoBox.appendRichText(str, {color: color.darker()});
    }
    this.infoBox.fit.bind(this.infoBox).delay(0);
}

