function browse() {
    if (!this.selectedNode) return;
    var test = this.selectedNode.item.test;
    if (test) {
        var testClass = test.constructor;
        var selector = test.currentSelector;
        lively.ide.browse(testClass.type, selector, testClass.sourceModule.name())
    } else {
        // try to find class with name
        var path = this.getPath(this.selectedNode.item).substring(1);
        var clazz = lively.Class.forName(path.replace(/\//g, '.'));
        if (clazz && lively.Class.isClass(clazz)) {
            lively.ide.browse(clazz.type, '', clazz.sourceModule.name())
        } else if (clazz instanceof lively.Module) {
            lively.ide.browse(clazz.uri());
        } else {
            lively.ide.browse(path);
        }
    }
}

