function addClasses(classes) {
    var testList = this.get("TestList");
    classes
        .select(function(ea) { return ea.isRunnableTestCaseClass(); })
        .select(function(clazz) { return clazz.sourceModule !== Global })
        .map(function(clazz) { return (new clazz()).createTests(); })
        .flatten()
        .each(function(test) { testList.addTest(test); });
}
