function drawStackChart(w, h) {
    w = w - 30;
    h = h - 25;

    var maxX = this.data[0].length,
        maxY = this.maxYofData(this.data) || 10;

    var x = pv.Scale.linear(0, maxX).range(0, w),
        y = pv.Scale.linear(0, maxY).range(0, h);

    var vis = new pv.Panel()
        .width(w)
        .height(h)
        .bottom(20)
        .left(20)
        .right(10)
        .top(5);

    /* X-axis and ticks. */
    vis.add(pv.Rule)
        .data(x.ticks())
        .visible(function(d){return d})
        .left(x)
        .bottom(-5)
        .height(5)
      .anchor("bottom").add(pv.Label)
        .text(x.tickFormat);

    /* The stack layout. */
    vis.add(pv.Layout.Stack)
        .layers(this.data)
        .x(function(d){return x(d.x)})
        .y(function(d){return y(d.y)})
      .layer.add(pv.Area);

    /* Y-axis and ticks. */
    vis.add(pv.Rule)
        .data(y.ticks(3))
        .bottom(y)
        .strokeStyle(function(d){return d ? "rgba(128,128,128,.2)" : "#000"})
      .anchor("left").add(pv.Label)
        .text(y.tickFormat);

    return vis;
}

