function loadVersions(finishedCallback) {
    // delete Global.WebResourceVersionCache
    // this.loadVersions()
    if (!Global.WebResourceVersionCache) {
        Global.WebResourceVersionCache = {};
    }

    var urlString = this.get('urlText').textString;
    if (!Global.WebResourceVersionCache[urlString]) {
        Global.WebResourceVersionCache[urlString] = {};
    }

    var progress = this.world().addStatusProgress("load revisions"),
        i = 0;
    // FIXME why is versions global???
    versions.doAndContinue(function(next, ea) {
        var rev = ea.rev;
        progress.updateBar(i++ / versions.length)

        if (!Global.WebResourceVersionCache[urlString][rev]) {
            var wr = new WebResource(urlString).beAsync();
            var updater = {
                update: function(s) {
                    Global.WebResourceVersionCache[urlString][rev] = s;
                    next();
                }
            }
            connect(wr, 'content' , updater, 'update');
            wr.get(rev);
        } else {
            next();
        }
    }, function() {
        progress.remove()
        finishedCallback(Global.WebResourceVersionCache[urlString])
    })
}

