function showHistory() {
    // this.showHistory()
    this.loadVersions(function(revs) {
        var vis = this.get('RevHistoryGraph').get('ProtovisMorph')

        module('apps.DiffMatchPatch').load(true)
        var dmp = new diff_match_patch()

        // Testing: 
        // revs = Global.WebResourceVersionCache['http://lively-kernel.org/repository/webwerkstatt/lively/morphic/Connectors.js']
        // revA = Properties.own(revs)[0]
        // revB = Properties.own(revs)[10]
        var revDiffs = [];
	Properties.own(revs).inject(null, function(revA, revB) {
            if (revA && revB) {
                var a = revs[revA];
        	    var b = revs[revB];
    	        var diffs = dmp.diff_lineMode(a,b)
                revDiffs.push(diffs)
            }
            return revB 
        })

        vis.data = []
        var i=0;
        vis.data.push(Properties.own(revs).collect(function(ea) {
                return {x: i++, y:  Strings.lines(revs[ea]).length}
        }))

        var i=0;
        vis.data.push(Properties.own(revs).collect(function(ea) {
                var addedLines =  0;
                var diffs = revDiffs[i];    
                if (diffs)
                    diffs.forEach(function(ea) { if (ea[0] == 1)
                            addedLines += Strings.lines(ea[1]).length
                    })
                return {x: i++, y:  addedLines * 1}
            }))
        var i=0;
        vis.data.push(Properties.own(revs).collect(function(ea) {
                var removedLines =  0;
                var diffs = revDiffs[i];    
                if (diffs)
                    diffs.forEach(function(ea) { if (ea[0] == -1)
                            removedLines += Strings.lines(ea[1]).length
                    })
                return {x: i++, y:  removedLines * 1}
            }))    

        vis.render()
    }.bind(this))
}

