function updateLinks() {
// this.updateLinks()
    wr = new WebResource(URL.source.getDirectory())
    wr.getSubElements(0)
    var links = wr.subDocuments.invoke('getURL').select(function(ea) {
        return ea.filename().match(/.*\.xhtml/) 
    }).reject(function(ea) {
        return ea.filename().match(/index.xhtml/) || ea.filename().match(/template.xhtml/)
    })

    items = links.sort().collect(function(ea) {
        return { 
            string: ea.filename().replace(".xhtml", ""),
            value: ea.toString()
        }
    })
    var rt = new lively.morphic.RichText("")
    rt.setList(items)

    this.get('LinksText').setRichText(rt)

}

