function updateLinks() {
// this.updateLinks()
    var rootURL = this.targetURL || URL.source.getDirectory();
    var wr = new WebResource(rootURL)
    try {
        wr.getSubElements(0)
    } catch(e) {
        alertOK("could not go to " + rootURL)
        return
    }
    var items = []
    var dirs = (wr.subCollections || []).invoke('getURL').sort()

    if (rootURL.toString() != URL.root.toString()) {
        items.push({
            string: '..',
            value: rootURL.withFilename("../").withRelativePartsResolved()
        })
    }
    items.pushAll(dirs.collect(function(ea) {
        return {
            string: ea.filename(),
            value: ea,
        }
    }))
    var links = (wr.subDocuments || []).invoke('getURL').select(function(ea) {
        return ea.filename().match(/.*\.xhtml/) 
    }).reject(function(ea) {
        return ea.filename().match(/index.xhtml/) || ea.filename().match(/template.xhtml/)
    })
    items.pushAll(links.sort().collect(function(ea) {
        return { 
            string: ea.filename().replace(".xhtml", ""),
            value: ea,
        }
    }))
    this.get('List').setList(items)

}

