function onLoad(propfindXML) {
    this.notify('Please wait, extracting version infos');

    if (propfindXML == '') {
        this.notify('Error: Could not find any changes.');
        return;
    }

    // extract
    var rawNodes = new Query("/D:multistatus/D:response").findAll(propfindXML.documentElement);

    var svnVersionInfos = rawNodes.map(function(rawNode) { return SVNVersionInfo.fromPropfindNode(rawNode) });

    this.svnVersionInfos = svnVersionInfos;

    this.updateChangesList();
}

