function createListItemFor(versionInfo) {
    module('apps.DateFormat').load(true);
    return {
        isListItem: true,
        string: versionInfo.shortName + ' (' + versionInfo.author + ', Rev. ' + versionInfo.rev + ', ' + versionInfo.date.format('mm/dd/yy HH:MM') + ', size: ' + versionInfo.fileSize + ' B)',
        value: {
            versionInfo: versionInfo,
            urlString: versionInfo.url,
        }
    };
}

