function updateChangesList() {
    // filter and sort
    var list = this.svnVersionInfos
        .select(function(ea) { return ea.shortName.match(this.getFileFilter()); }, this)
        .select(function(ea) { return ea.author.match(this.getAuthorFilter()); }, this)
        .collect(function(ea) { return this.createListItemFor(ea); }, this)
        .sort(function(a,b) { return b.value.versionInfo.rev - a.value.versionInfo.rev; })
        .slice(0, this.getMaxListLength());
    this.get('changesList').updateList(list);
}

