function openDifferForURL(url) {
    var differ = lively.PartsBin.getPart('VersionDiffer', 'PartsBin/NewWorld');
    if (!url.startsWith('http'))
        url = 'http://' + URL.codeBase.hostname + url
    differ.setURL(url);
    differ.openInWorld();
    differ.align(differ.bounds().topLeft(), this.bounds().bottomLeft())
}
