function updateLinks() {
// this.updateLinks()
    wr = new WebResource(URL.source.getDirectory())
    wr.getSubElements(0)
    var links = wr.subDocuments.invoke('getURL').select(function(ea) {
        return ea.filename().match(/.*\.xhtml/)
    })

    chunks = links.sort().reverse().collect(function(ea) {
        var linkName = ea.filename().replace(".xhtml", "")
         return new lively.morphic.TextChunk(linkName +'\n', 
            new lively.morphic.TextEmphasis({uri: ea.toString()}))
    })
    var rt = new lively.morphic.RichText("")
    rt.setTextChunks(chunks)

    this.get('LinksText').setRichText(rt)

}
