function moveWorlds() {
	var fromURL = new URL(this.get('fromURL').textString),
            toURL = new URL(this.get('toURL').textString),
            files;
        if (fromURL.isLeaf()) 
            files = [fromURL]
        else
            files = new WebResource(fromURL).getSubElements(3)
                .subDocuments.invoke('getURL');

        // files = files.select(function(ea) { return ea.filename().endsWith('.xhtml')})

        this.stopMoving = false;
        this.progressReset(files.length);
        this.get('Log').textString = ''

        files.doAndContinue(function(next, ea) {
            this.progressInc();
            var relative = ea.relativePathFrom(fromURL);
            var to = toURL.withFilename(relative)
            this.log("moved " + relative + ' to ' + to);            

            if (this.stopMoving) next = null
            if (!this.dryRun)  {
                LivelyMigrationSupport.moveFileFromTo(ea, to, 
                    function() {next.delay(0.1)})
            } else {
                if (next) next.delay(0.1)
            }


        }, null, this)
}

