function progressReset(valueScale) {
    var p = this.get('progressBar');
    p.valueScale = valueScale || 1;
    p.setValue(0)
    p.setLabel('')
}

430.source:
function progressInc() {
    var p = this.get('progressBar');
    p.setValue(p.value + 1);
    p.setLabel(p.value + '/' + p.valueScale);
}

